-------------------------------------------    ID: 4181--    Scroll of Instant Warp--  Transports the user to their Home Point-----------------------------------------require("scripts/globals/status");require("scripts/globals/teleports");------------------------------------------- OnItemCheck-----------------------------------------function onItemCheck(target)    return 0;end;------------------------------------------- OnItemUse-----------------------------------------function onItemUse(target)    target:delStatusEffect(EFFECT_STONESKIN);    target:addStatusEffect(EFFECT_STONESKIN, target:getMainLvl() * 5, 0, 3000);end;